    private boolean pausado = false;
    
    
    private void botonPausaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonPausaActionPerformed
        // TODO add your handling code here:
        if (pausado) {
            ControlGlobal.reanudar();
            botonPausa.setText("Pausar");
        } else {
            ControlGlobal.pausar();
            botonPausa.setText("Reanudar");
        }
        pausado = !pausado;
    }//GEN-LAST:event_botonPausaActionPerformed

    private void btnVerHumanosZ3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVerHumanosZ3ActionPerformed
        // TODO add your handling code here:
        List<String> humanos = ZonaInsegura.get(3).getHumanosPresentes();

        StringBuilder sb = new StringBuilder("Humanos en Zona Insegura 3:\n");
        for (String h : humanos) {
            sb.append(" - ").append(h).append("\n");
        }

        JOptionPane.showMessageDialog(this, sb.toString(), "Zona Insegura 3 - Humanos", JOptionPane.INFORMATION_MESSAGE);

    }//GEN-LAST:event_btnVerHumanosZ3ActionPerformed

