    private void btnVerZonaComunActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVerZonaComunActionPerformed
        // TODO add your handling code here:
        List<String> humanos = refugio.getZonaComun().getHumanosPresentes();
        String lista = humanos.isEmpty() ? "No hay humanos actualmente." : String.join("\n", humanos);
        JOptionPane.showMessageDialog(this, lista, "Zona Común", JOptionPane.INFORMATION_MESSAGE);

    }//GEN-LAST:event_btnVerZonaComunActionPerformed

